-- THIS IS MODIFIED VERSION OF CTLD by Ciribob, with added code to remove mist dependancies and add some automation.


local ModuleName  	= "TRPS_inj"
local MainVersion 	= DSMC_MainVersion or "missing, loaded without DSMC"
local SubVersion 	= DSMC_SubVersion or "missing, loaded without DSMC"
local Build 		= DSMC_Build or "missing, loaded without DSMC"
local Date			= DSMC_Date or "missing, loaded without DSMC"

--[[
    Combat Troop and Logistics Drop

    Allows Huey, Mi-8 and C130 to transport troops internally and Helicopters to transport Logistic / Vehicle units to the field via sling-loads
    without requiring external mods.

    Supports all of the original CTTS functionality such as AI auto troop load and unload as well as group spawning and preloading of troops into units.

    Supports deployment of Auto Lasing JTAC to the field

    See https://github.com/ciribob/CTLD for a user manual and the latest version

	Contributors:
	    - Steggles - https://github.com/Bob7heBuilder
	    - mvee - https://github.com/mvee
	    - jmontleon - https://github.com/jmontleon
	    - emilianomolina - https://github.com/emilianomolina

    Version: 1.73 - 15/04/2018
      - Allow minimum distance from friendly logistics to be set
 ]]

TRPS = {} -- DONT REMOVE!
local _debugMode = false
local debugProcessDetail = DSMC_debugProcessDetail or false

-- ************************************************************************
-- *********************  USER CONFIGURATION ******************************
-- ************************************************************************



--test variables often changed
TRPS.unpackRestriction      = true -- if false, you can unpack in logistic zone
TRPS.forceCrateToBeMoved    = false -- a crate must be picked up at least once and moved before it can be unpacked. Helps to reduce crate spam
TRPS.datespan               = 30 -- number of years that DSMC goes back to identify available units, using DCS database
TRPS.crateReductionFactor   = TRPScrateReductionFactor or 1.5 -- in this version the crates required to built a vehicle depends from the life points. the more you increase this value, the lesser crates are required
TRPS.crateLargeToSmallRatio = 3 -- this variable define how many small crates equals to large crates. "3" means that 3 small crates equals to 1 large crates.
TRPS.buildTimeFOB           = 240 --time in seconds for the FOB to be built
TRPS.crateWaitTime          = 5 -- time in seconds to wait before you can spawn another crate
TRPS.f10menuUpdateFreq      = 1 -- time in seconds between an F10 men√π update and the subsequent one
TRPS.beaconMinFreq          = 129000000
TRPS.beaconMaxFreq          = 139000000
TRPS.upscaleResupplyFactor  = 10 -- crate weight / warehouse resupply weight factor. if it's 10, means that 1 ton fuel tank goes to 10 tons fuel resupply.
TRPS.repairCrateModel       = "container_cargo"
TRPS.unpackCrateDistance    = 200 -- distance in meters between the heli and the nearest crate to allow an "unpack" action. It's also used to determinee if you moved the crate at least once after the crate creation.
TRPS.unpackTemplateDistance = 750 -- distance in meters between the crates to be identified as ok for a template (else, you will need to group them more). This also apply for FOB crates


if _debugMode == true then
    TRPS.unpackRestriction = false -- if false, you can unpack in logistic zone
    TRPS.forceCrateToBeMoved = false -- a crate must be picked up at least once and moved before it can be unpacked. Helps to reduce crate spam
    TRPS.crateLargeToSmallRatio = 1
    TRPS.crateReductionFactor = 2
    TRPS.buildTimeFOB = 10 --time in seconds for the FOB to be built
    TRPS.crateWaitTime = 1 -- time in seconds to wait before you can spawn another crate    
end

TRPS.enableCrates           = TRPSallowCrates_main or false -- if false, Helis will not be able to spawn or unpack crates so will be normal CTTS
TRPS.slingLoad              = TRPSslingLoad_main or false -- if false, crates can be used WITHOUT slingloading, by hovering above the crate, simulating slingloading but not the weight...
TRPS.UnitNumLimits          = TRPSUnitNumLimits or false
TRPS.allowPlatoons          = TRPSallowPlatoons_main or false -- true, added by Chromium. If set, allow platoon of vehicles creation
TRPS.useYearFilter          = TRPSuseYearFilter or false -- if true, in mission you will have only allowed units for country used in ED database
TRPS.dbYears                = TRPSdbYears
TRPS.factoryok              = {}

-- if SP, then platoons are on!
if not DSMC_multy then
    TRPS.allowPlatoons = true
end

if TRPS.dbYears then
    TRPSdbYears = nil
    env.info(ModuleName .. " TRPS.dbYears loaded by hooks")
    local missionYear = tonumber(env.mission.date.Year)
    for wType, wData in pairs(TRPS.dbYears) do
        
        local noTruck = true
        if wType == "M 818" or wType == "GAZ-66" then

            noTruck = false

            TRPS.dbYears[wType] = {
                ["ALL"] = 
                {
                    ["yStart"] = 1900,
                    ["yEnd"] = 9999,
                    ["origin_country"] = 1,
                }, 
            }
        end

        if noTruck == true then
            for cId, cData in pairs(wData) do    
                if cData.yStart < (missionYear+1) then

                    local isThere = false

                    for _coalitionName, _coalitionData in pairs(env.mission.coalition) do
                        if (_coalitionName == 'red' or _coalitionName == 'blue')
                                and type(_coalitionData) == 'table' then
                            if _coalitionData.country then --there is a country table
                                for _, _countryData in pairs(_coalitionData.country) do
                                    if type(_countryData) == 'table' then
                                        if _countryData.helicopter or _countryData.plane or _countryData.static or _countryData.vehicle then
                                            if string.lower(cId) == string.lower(_countryData.name) or string.lower(cId) == "ALL" then
                                                isThere = true
                                            end
                                        end
                                    end
                                end
                            end
                        end
                    end

                    if isThere == false then
                        wData[cId] = nil
                    end

                else
                    wData[cId] = nil
                end
            end
        end
    end

    for wType, wData in pairs(TRPS.dbYears) do
        if next(wData) == nil then
            TRPS.dbYears[wType] = nil
        end
    end
