function TRPS.msgNearestLogistic(vars)
    local _heliName = vars[1]

    local _heli = Unit.getByName(_heliName)

    if _heli then
        local _heliPoint = _heli:getPoint()

        local _nearestPos = nil
        local _nearestDeg = nil
        local _nearestDist = 100000000
        for _, _name in pairs(TRPS.logisticUnits) do
            
            local _logistic = nil
            _logistic = Airbase.getByName(_name)
            if not _logistic then
                _logistic = StaticObject.getByName(_name) 
            end

            if _logistic then
                local coaTest = _logistic:getCoalition()
                

                if _logistic ~= nil and _logistic:getCoalition() == _heli:getCoalition() then
                    local _logPoint = _logistic:getPoint()
                    local _dist = TRPS.getDistance(_heliPoint, _logPoint)
                    local _deg = math.floor(math.deg(math.atan2(_logPoint.z-_heliPoint.z, _logPoint.x-_heliPoint.x))%360)

                    if _dist < _nearestDist then
                        _nearestDist = _dist
                        _nearestDeg = _deg
                        _nearestPos = _logPoint
                    end
                end
            end
        end

        if _nearestPos and _nearestDist and _nearestDeg then
            local km = math.floor(_nearestDist/100)/10
            local _lat, _lon = coord.LOtoLL(_nearestPos)
            local _latLngStr = TRPS.tostringLL(_lat, _lon, 3, false)
            local _message = "Nearest logistic is located at " .. _latLngStr .. ", bearing " .. _nearestDeg .. "¬∞, " .. km .. " km"
            
            TRPS.displayMessageToGroup(_heli, _message, 20)
            return true

        else
            local _message = "No logistic site available"
            TRPS.displayMessageToGroup(_heli, _message, 20)
            return false
        end
    else
        local _message = "No sufficient info available"
        TRPS.displayMessageToGroup(_heli, _message, 20)
        return false
    end
end

