function TRPS.loadMultiTroops(_unit, _troops, _numberOrTemplate)
    if _troops == false then
        if _unit:hasAttribute("APC") or _unit:hasAttribute("IFV") or _unit:hasAttribute("Trucks") then
            local _group = _unit:getGroup()
            if _group then
                local _units = _group:getUnits()
                if _units then
                    for _, uData in pairs(_units) do
                        local uName = uData:getName()
                        env.info(ModuleName .. " loadMultiTroops uName: " .. tostring(uName))
                        local u_heli = TRPS.getTransportUnit(uName)

                        if u_heli then
                            env.info(ModuleName .. " loadMultiTroops uName: done")
                            TRPS.loadTroops(u_heli, _troops, _numberOrTemplate)
                        else
                            env.info(ModuleName .. " loadMultiTroops u_heli missing")
                        end

                    end
                else
                    env.info(ModuleName .. " loadMultiTroops _units missing")
                end
            else
                env.info(ModuleName .. " loadMultiTroops _group missing")
            end
        else
            env.info(ModuleName .. " loadMultiTroops is a plane-helo")
            TRPS.loadTroops(_unit, _troops, _numberOrTemplate)
        end
    else
        env.info(ModuleName .. " loadMultiTroops is for vehicles, multiple not admitted")
        TRPS.loadTroops(_unit, _troops, _numberOrTemplate)
    end        
end

function TRPS.extractMultiTroops(_args) -- TEST DSMC
    local _unit = TRPS.getTransportUnit(_args[1]) 
    if _unit:hasAttribute("APC") or _unit:hasAttribute("IFV") or _unit:hasAttribute("Trucks") then
        local _group = _unit:getGroup()
        if _group then
            local _units = _group:getUnits()
            if _units then
                for _, uData in pairs(_units) do
                    local uName = uData:getName()
                    env.info(ModuleName .. " extractMultiTroops uName: " .. tostring(uName))
