function TRPS.loadMultyCrate(_name)
    local _unit = TRPS.getTransportUnit(_name)
    if _unit:hasAttribute("APC") or _unit:hasAttribute("IFV") or _unit:hasAttribute("Trucks") then
        local _group = _unit:getGroup()
        if _group then
            local _units = _group:getUnits()
            if _units then
                for _, uData in pairs(_units) do
                    local uName = uData:getName()
                    env.info(ModuleName .. " loadMultyCrate uName: " .. tostring(uName))
                    TRPS.loadNearbyCrate(uName)
                end
            else
                env.info(ModuleName .. " loadMultyCrate _units missing")
            end
        else
            env.info(ModuleName .. " loadMultyCrate _group missing")
        end
    else
        env.info(ModuleName .. " loadMultyCrate is a plane-helo")
        TRPS.loadNearbyCrate(_name)
    end
end


