--includes fob crates!
function TRPS.getCratesAndDistance(_heli, _distanceFilter)
    local _crates = {}

    local _allCrates
    if _heli:getCoalition() == 1 then
        _allCrates = TRPS.spawnedCratesRED
    elseif _heli:getCoalition() == 2 then
        _allCrates = TRPS.spawnedCratesBLUE
    else
        _allCrates = TRPS.spawnedCratesNEUTRAL
    end

    for _crateName, _details in pairs(_allCrates) do
        --get crate
        local _crate = TRPS.getCrateObject(_crateName)

        --in air seems buggy with crates so if in air is true, get the height above ground and the speed magnitude
        if _crate ~= nil and _crate:getLife() > 0
                and (TRPS.inAir(_crate) == false) then

            local _dist = TRPS.getDistance(_crate:getPoint(), _heli:getPoint())

            local proceed = false
            if _distanceFilter then
                if _dist <= _distanceFilter then
                    proceed = true
                end
            else
                proceed = true
            end


            if proceed then
                local _crateDetails = { crateUnit = _crate, dist = _dist, details = _details }
                table.insert(_crates, _crateDetails)
            end
        end
    end

    local _fobCrates
    if _heli:getCoalition() == 1 then
        _fobCrates = TRPS.droppedFOBCratesRED
    elseif _heli:getCoalition() == 2 then
        _fobCrates = TRPS.droppedFOBCratesBLUE
    else
        _fobCrates = TRPS.droppedFOBCratesNEUTRAL
    end

    for _crateName, _details in pairs(_fobCrates) do

        --get crate
        local _crate = TRPS.getCrateObject(_crateName)

        if _crate ~= nil and _crate:getLife() > 0 then

            local _dist = TRPS.getDistance(_crate:getPoint(), _heli:getPoint())

            local _crateDetails = { crateUnit = _crate, dist = _dist, details = { unit = "FOB" }, }

            table.insert(_crates, _crateDetails)
        end
    end

    return _crates
    
end

function TRPS.getClosestCrate(_heli, _crates, _type)
    if _crates then
        if #_crates > 0 then
            local _closetCrate = nil
            local _shortestDistance = -1
            local _distance = 0

            for _, _crate in pairs(_crates) do

                if (_crate.details.unit == _type or _type == nil) then
                    _distance = _crate.dist

                    if _distance ~= nil and (_shortestDistance == -1 or _distance < _shortestDistance) then
                        _shortestDistance = _distance
                        _closetCrate = _crate
                    end
                end
            end

            return _closetCrate
        end
    end
end

function TRPS.cleanAreaFromWreckage(_Crate)

    if _Crate and _Crate.crateUnit then
        local vec3 = _Crate.crateUnit:getPosition().p
        if vec3 then

            local _volume = {
                id = world.VolumeType.SPHERE,
                params = {
                    point = vec3,
                    radius = TRPS.unpackTemplateDistance*2
                }
            }

            local _search = function(_static)
                pcall(function()
        
                    if _static ~= nil and _static:getLife() < 1 then
                        _static:destroy()
                        env.info("TRPS cleanAreaFromWreckage: destroyed stati object cleaned out")
                    end
                end)
        
                return true
            end
            world.searchObjects(Object.Category.STATIC, _volume, _search)

        end
    end
end

function TRPS.findNearestTemplate(_Heli, _Crate, _Template)

    local _templateParts = {}
    local _unitList = {}
    local _group = _Heli:getGroup()
    local _heliUnits = _group:getUnits()

    if _Crate and _Crate.crateUnit and _Template.parts then
        local vec3 = _Crate.crateUnit:getPosition().p
        local _coa = _Crate.crateUnit:getCoalition()
        if vec3 and _coa then

            local _volume = {
                id = world.VolumeType.SPHERE,
                params = {
                    point = vec3,
                    radius = TRPS.unpackTemplateDistance*2
                }
            }

            for xId, xData in pairs (_Template.parts) do
                _templateParts[#_templateParts+1] = xData
            end
            env.info("TRPS findNearestTemplate: _templateParts (initial) " .. tostring(#_templateParts))  

            -- count
            local _search = function(_unit, _coa)
                pcall(function()
        
                    if _unit ~= nil and _unit:getLife() > 0 and _unit:getCoalition() == _coa then

                        local halt = false -- this for preventing units from the "player" group to be rmoved XD
                        for tId, tData in pairs(_heliUnits) do
                            if _unit:getID() == tData:getID() then
                                halt = true
                            end
                        end

                        if not halt then
                            local _tempPoint = _unit:getPoint()
                            local _Type = _unit:getTypeName()
                            local _Point = { x = _tempPoint.x, y = _tempPoint.y, z = _tempPoint.z }

                            for pId, pData in pairs (_Template.parts) do
                                if _Type == pData.name then
                                    
                                    env.info("TRPS findNearestTemplate: name: " .. tostring(pData.name))
                                    _unitList[#_unitList+1] = { u = _unit, t = _Type, p = _Point, l = pData.launcher} -- la conta dei lanciatori non funziona!!!!!!
                                    
                                    for dId, dData in pairs (_templateParts) do
                                        if dData.name == _Type then
                                            table.remove(_templateParts, dId)
                                        end
                                    end
                                    
                                end
                            end



                        end

                    end
                end)
        
                return true
            end
            world.searchObjects(Object.Category.UNIT, _volume, _search, _coa)
            
        end
    end

    local msg = ""
    env.info("TRPS findNearestTemplate: _templateParts " .. tostring(#_templateParts))  
    env.info("TRPS findNearestTemplate: _unitList " .. tostring(#_unitList))  
    
    if #_templateParts == 0 then
        damageRatio = 0
        msg = "No damaged group found"       
        env.info("TRPS findNearestTemplate: No damaged group found"   )  
    elseif #_unitList > 0 and #_templateParts > 0 then
        env.info("TRPS findNearestTemplate: returning parts"   ) 
        return _unitList, _Template
    else
        env.info("TRPS findNearestTemplate: no templates found"   ) 
        return msg
    end
end

