function TRPS.unpackTemplate(_heli, _nearestCrate, _nearbyCrates, _CustomTemplate)

    --if TRPS.rearmAASystem(_heli, _nearestCrate, _nearbyCrates,_CustomTemplate) then
    --      -- rearmed hawk
    --      return
    --      env.info(TRPS.pickupZones[_index][1].." = " ..TRPS.pickupZones[_index][3])
    --end

    env.info(ModuleName .. " unpackTemplate: trying to found template of " .. tostring(_CustomTemplate.name))

    -- are there all the pieces close enough together
    local _systemParts = {}

    --initialise list of parts
    for _,_part in pairs(_CustomTemplate.parts) do
        _systemParts[_part.name] = {name = _part.name, desc = _part.desc, found = false, ln = _part.launcher}
        env.info(ModuleName .. " unpackTemplate: _systemParts added " .. tostring(_part.name))
    end

    -- find all nearest crates and add them to the list if they're part of the AA System -- QUIII
    local _lnPart = 0

    local killCratesTable = {}
    for _, _nearbyCrate in pairs(_nearbyCrates) do

        if _nearbyCrate.dist < TRPS.unpackTemplateDistance then

            env.info(ModuleName .. " unpackTemplate: _nearbyCrate details.unit " .. tostring(_nearbyCrate.details.unit))

            if _systemParts[_nearbyCrate.details.unit] ~= nil then
                local proceed = false
                if _nearbyCrate.details.isLauncher then
                    _lnPart = _lnPart + 1
                    proceed = true
                else
                    if _systemParts[_nearbyCrate.details.unit].found == false then
                        proceed = true
                    end
                end                
                env.info(ModuleName .. " unpackTemplate: proceed " .. tostring(proceed))

                if proceed then
                    killCratesTable[#killCratesTable+1] = _nearbyCrate.crateUnit
                    local _foundPart = _systemParts[_nearbyCrate.details.unit]
                    _foundPart.found = true
                    _foundPart.crate = _nearbyCrate
                    _systemParts[_nearbyCrate.details.unit] = _foundPart
                    env.info(ModuleName .. " unpackTemplate: _systemParts: added part " .. tostring(_nearbyCrate.details.unit))
                end
            end
        end
    end

    local _count = 0
    local _txt = ""

    local _posArray = {}
    local _typeArray = {}

    env.info(ModuleName .. " unpackTemplate: _lnPart: " .. tostring(_lnPart))

    for _name, _systemPart in pairs(_systemParts) do
        env.info(ModuleName .. " unpackTemplate: _systemPart: adding _name " .. tostring(_name))

        if _systemPart.found == false then
            _txt = _txt.."Missing ".._systemPart.desc.."\n"
            env.info(ModuleName .. " unpackTemplate: _systemPart: part missing")
        else

            local _launcherPart = TRPS.getLauncherUnitFromAATemplate(_CustomTemplate)

            --handle multiple launchers from one crate
            if (_launcherPart == _name and TRPS.aaLaunchers  > 1) then

                --add multiple launcher
                local _launchers = TRPS.aaLaunchers + _lnPart - 1

                for _i = 1, _launchers do

                    -- spawn in a circle around the crate
                    local _angle = math.pi * 2 * (_i - 1) / _launchers
                    local _xOffset = math.cos(_angle) * 12
                    local _yOffset = math.sin(_angle) * 12

                    local _point = _systemPart.crate.crateUnit:getPoint()

                    _point = { x = _point.x + _xOffset, y = _point.y, z = _point.z + _yOffset }

                    table.insert(_posArray, _point)
                    table.insert(_typeArray, _name)
                    env.info(ModuleName .. " unpackTemplate: _systemPart: inserted launcher")
                end
            else
                table.insert(_posArray, _systemPart.crate.crateUnit:getPoint())
                table.insert(_typeArray, _name)
                env.info(ModuleName .. " unpackTemplate: _systemPart: inserted something else")
            end
        end
    end

    local _activeSAM_site = TRPS.countCompleteAASystems(_heli)

    local _allowed = TRPS.SAM_site_limit

    --env.info("Active: ".._activeSAM_site.." Allowed: ".._allowed)

    if _activeSAM_site + 1 > _allowed then
        trigger.action.outTextForCoalition(_heli:getCoalition(), "Out of parts for SAM sites. Current limit is ".._allowed.." \n", 10)
        env.info(ModuleName .. " unpackTemplate: too many sams out there, stopping")
        return
    end

    if _txt ~= ""  then
        TRPS.displayMessageToGroup(_heli, "Cannot build ".._CustomTemplate.name.."\n" .. _txt .. "\n\nOr the crates are not close enough together", 20)
        env.info(ModuleName .. " unpackTemplate: Cannot build ".._CustomTemplate.name.."\n" .. _txt .. "\n\nOr the crates are not close enough together")
        return
    else

        -- destroy crates
        for _, crateUnit in pairs(killCratesTable) do
            if crateUnit then
                if _heli:getCoalition() == 1 then
                    TRPS.spawnedCratesRED[crateUnit:getName()] = nil
                elseif _heli:getCoalition() == 2 then
                    TRPS.spawnedCratesBLUE[crateUnit:getName()] = nil
                else
                    TRPS.spawnedCratesNEUTRAL[crateUnit:getName()] = nil
                end                

                if tblDeadUnits then
                    tblDeadUnits[#tblDeadUnits + 1] = {unitId = crateUnit:getID(), objCategory = 6}
                end                

                crateUnit:destroy()
            end
        end


        --[[
        for _name, _systemPart in pairs(_systemParts) do

            if _heli:getCoalition() == 1 then
                TRPS.spawnedCratesRED[_systemPart.crate.crateUnit:getName()] = nil
            elseif _heli:getCoalition() == 2 then
                TRPS.spawnedCratesBLUE[_systemPart.crate.crateUnit:getName()] = nil
            else
                TRPS.spawnedCratesNEUTRAL[_systemPart.crate.crateUnit:getName()] = nil
            end

            -- DSMC addition to track old crates
            if tblDeadUnits then
                tblDeadUnits[#tblDeadUnits + 1] = {unitId = _systemPart.crate.crateUnit:getID(), objCategory = 6}
            end
            _systemPart.crate.crateUnit:destroy()

        end
        --]]--

        -- HAWK / BUK READY!
        local _spawnedGroup = TRPS.spawnCrateGroup(_heli, _posArray, _typeArray)

        TRPS.completedTemplate[_spawnedGroup:getName()] = TRPS.getCustomTemplateDetails(_spawnedGroup,_CustomTemplate)

        TRPS.processCallback({unit = _heli, crate = _nearestCrate , spawnedGroup = _spawnedGroup, action = "unpack"})

        trigger.action.outTextForCoalition(_heli:getCoalition(), TRPS.getPlayerNameOrType(_heli) .. " successfully deployed a full ".._CustomTemplate.name.." to the field. \n\nSAM site limit is: ".._allowed.."\nActive: "..(_activeSAM_site+1), 10)

    end
end

function TRPS.countCompleteAASystems(_heli)     -- MODIFY TO CHECK SAMS!!!

    local _count = 0

    for _groupName, _templateDetails in pairs(TRPS.completedTemplate) do

        local _hawkGroup = Group.getByName(_groupName)

        if _hawkGroup ~= nil and _hawkGroup:getCoalition() == _heli:getCoalition() then

            local _units = _hawkGroup:getUnits()
            local _isSamSite = false
            if TRPS.countTableEntries(_units) > 0 then
                for _, _unit in pairs(_units) do
                    if _unit:hasAttribute("SAM LL") then
                        _isSamSite = true
                    end
                end
            end

            if _isSamSite == true then
                _count = _count +1
            end
        end
    end

    return _count
end

function TRPS.repairByCrate(_heli, _crate)

    if _crate and _heli then
        local nogroup = false
        for _,  templateData in pairs(TRPS.specificTemplate) do -- WUIII
            env.info("TRPS repairByCrate: chcking template " .. tostring(templateData.name))

            local units, template = TRPS.findNearestTemplate(_heli, _crate, templateData)
            if type(units) == "string" then
                nogroup = true
            elseif type(units) == "table" and template then

                -- remove existing dead units
                for _, uData in pairs (units) do
                    if uData.u then
                        local uObj = uData.u
                        local uLife = uObj:getLife()
                        if uLife > 1 then
                            uObj:destroy()


                            -- add necessary to track unit dead also in DSMC
                            if tblDeadUnits then
                                tblDeadUnits[#tblDeadUnits + 1] = {unitId = uObj:getID(), objCategory = 1}
                            end

                        end
                    end
                end

                -- spawn new one
                local nUtbl = TRPS.deepCopy(template.parts)

                local primaPos = nil
                for pId, pData in pairs(nUtbl) do
                    for uId, uData in pairs(units) do
                        if pData.name == uData.t then
                            pData.pos = uData.p
                            pData.launcher = uData.l
                            primaPos = uData.p
                            env.info("TRPS repairByCrate: added identified pos to unit " .. tostring(pData.name))
                        end
                    end
                end

                -- recycle to assign missing position
                local missingPos = 0
                if primaPos then
                    if primaPos.x and primaPos.y and primaPos.z then
                        for pId, pData in pairs(nUtbl) do
                            if not pData.pos then
                                missingPos = missingPos + 1
                                env.info("TRPS repairByCrate: identified missing position for " .. tostring(pData.name))
                            end
                        end
                    end
                end

                if missingPos > 0 then

                    local pTbl = {}
                    for _i = 1, missingPos do
                        local _angle = math.pi * 2 * (_i - 1) / missingPos
                        local _xOffset = math.cos(_angle) * 12
                        local _yOffset = math.sin(_angle) * 12

                        local _point = primaPos
                        _point = { x = _point.x + _xOffset, y = _point.y, z = _point.z + _yOffset }
                        pTbl[_i] = _point
                        --table.insert(pTbl, _point)
                    end

                    -- riaggiungi punti
                    local ix = 1
                    for pId, pData in pairs(nUtbl) do
                        if not pData.pos then
                            pData.pos = pTbl[ix]
                            ix = ix + 1
                            env.info("TRPS repairByCrate: added new pos to unit " .. tostring(pData.name))
                        end
                    end

                    -- built group table
                    local _posArray = {}
                    local _typeArray = {}
                    local errorcheck = false
                    for pId, pData in pairs(nUtbl) do
                        if pData.pos and pData.name then
                            if pData.launcher then

                                for _i = 1, 3 do
                                    local _angle = math.pi * 2 * (_i - 1) / 3
                                    local _xOffset = math.cos(_angle) * 12
                                    local _yOffset = math.sin(_angle) * 12
                
                                    --local _point = { x = _point.x + _xOffset, y = _point.y, z = _point.z + _yOffset }
                                    local _dePos = { x = pData.pos.x + _xOffset, y = pData.pos.y, z = pData.pos.z + _yOffset }
                                    
                                    table.insert(_posArray, _dePos)
                                    table.insert(_typeArray, pData.name)    
                                end

                            else
                                env.info("TRPS repairByCrate: added toarray data for unit " .. tostring(pData.name))
                                table.insert(_posArray, pData.pos)
                                table.insert(_typeArray, pData.name)    
                            end
                        else
                            env.info("TRPS repairByCrate: missing data of pos or t for arrays, unit: " .. tostring(pData.name))
                            errorcheck = true              
                        end
                    end


                    -- exec
                    if not errorcheck then

                        -- remove wreckage if present
                        TRPS.cleanAreaFromWreckage(_crate)

                        local _spawnedGroup = TRPS.spawnCrateGroup(_heli, _posArray, _typeArray)

                        TRPS.completedTemplate[_spawnedGroup:getName()] = TRPS.getCustomTemplateDetails(_spawnedGroup, templateData)
                        TRPS.processCallback({unit = _heli, crate = _crate , spawnedGroup = _spawnedGroup, action = "unpack"})
                
                        trigger.action.outTextForCoalition(_heli:getCoalition(), TRPS.getPlayerNameOrType(_heli) .. " successfully repaired "..templateData.name, 10)

                        _crate.crateUnit:destroy()

                        env.info("TRPS repairByCrate: process complete, repaired: " .. tostring(templateData.name)) 
                    else
                        env.info("TRPS repairByCrate: errors in spawning a new group")
                    end
                else
                    env.info("TRPS repairByCrate: no repair needed")
                    trigger.action.outTextForCoalition(_heli:getCoalition(), TRPS.getPlayerNameOrType(_heli) .. " no repair needed for "..templateData.name, 10)

                end
            end
        end

        if nogroup == true then
            env.info("TRPS repairByCrate: template search returning message, printing no template found")
            TRPS.displayMessageToGroup(_heli, "No damaged template found. Repair crate won't be removed: you can move it somewhere else.", 10)
        end

    else
        env.info("TRPS repairByCrate: crate variable is missing")
    end
end

function TRPS.unpackMultiCrate(_heli, _nearestCrate, _nearbyCrates)

    -- unpack multi crate
    local _nearbyMultiCrates = {}

    for _, _nearbyCrate in pairs(_nearbyCrates) do

        if _nearbyCrate.dist < 300 then

            if _nearbyCrate.details.unit == _nearestCrate.details.unit then

                table.insert(_nearbyMultiCrates, _nearbyCrate)

                if #_nearbyMultiCrates == _nearestCrate.details.cratesRequired then
                    break
                end
            end
        end
    end

    --- check crate count
    if #_nearbyMultiCrates == _nearestCrate.details.cratesRequired then

        local _point = _nearestCrate.crateUnit:getPoint()

        -- destroy crates
        for _, _crate in pairs(_nearbyMultiCrates) do

            if _point == nil then
                _point = _crate.crateUnit:getPoint()
            end

            if _heli:getCoalition() == 1 then
                TRPS.spawnedCratesRED[_crate.crateUnit:getName()] = nil
            elseif _heli:getCoalition() == 2 then
                TRPS.spawnedCratesBLUE[_crate.crateUnit:getName()] = nil
            else
                TRPS.spawnedCratesNEUTRAL[_crate.crateUnit:getName()] = nil
            end

            _crate.crateUnit:destroy()

        end

        local _pointsArray = {}
        local _typeArray = {}
        local _multyUnit = false
        if  _nearestCrate.details.quantity then
            
            for _i = 1, _nearestCrate.details.quantity do
                -- spawn in a circle around the crate
                local _angle = math.pi * 2 * (_i - 1) / _nearestCrate.details.quantity
                local _xOffset = math.cos(_angle) * 12
                local _yOffset = math.sin(_angle) * 12

                local _ipoint = _point

                _ipoint = { x = _ipoint.x + _xOffset, y = _ipoint.y, z = _ipoint.z + _yOffset }

                table.insert(_pointsArray, _ipoint)
                table.insert(_typeArray, _nearestCrate.details.unit)       
            end

            if table.getn(_pointsArray) > 1 then
                if table.getn(_pointsArray) == table.getn(_typeArray) then
                    _multyUnit = true
                end
            end

        end

        local _spawnedGroup = nil
        if _multyUnit == true then
            _spawnedGroup = TRPS.spawnCrateGroup(_heli, _pointsArray, _typeArray)
        else
           _spawnedGroup = TRPS.spawnCrateGroup(_heli, { _point }, { _nearestCrate.details.unit })
        end

        TRPS.processCallback({unit = _heli, crate =  _nearestCrate , spawnedGroup = _spawnedGroup, action = "unpack"})

        local _txt = string.format("%s successfully deployed %s to the field using %d crates", TRPS.getPlayerNameOrType(_heli), _nearestCrate.details.desc, #_nearbyMultiCrates)

        trigger.action.outTextForCoalition(_heli:getCoalition(), _txt, 10)

    else

        local _txt = string.format("Cannot build %s!\n\nIt requires %d crates and there are %d \n\nOr the crates are not within 300m of each other", _nearestCrate.details.desc, _nearestCrate.details.cratesRequired, #_nearbyMultiCrates)

        TRPS.displayMessageToGroup(_heli, _txt, 20)
    end
end

